import { colors } from "@atlaskit/theme";
import finnImg from "./static/finn-min.png";
import bmoImg from "./static/bmo-min.png";
import princessImg from "./static/princess-min.png";
import jakeImg from "./static/jake-min.png";

const jake = {
  id: "1",
  name: "ترسیم رویاها",

  avatarUrl: jakeImg,
  colors: {
    soft: colors.Y50,
    hard: colors.N400A
  }
};

const BMO = {
  id: "2",
  name: "برنامه های انجام شده ",

  avatarUrl: bmoImg,
  colors: {
    soft: colors.G50,
    hard: colors.N400A
  }
};

const finn = {
  id: "3",
  name: "برنامه های فوری",

  avatarUrl: finnImg,
  colors: {
    soft: colors.B50,
    hard: colors.N400A
  }
};

const princess = {
  id: "4",
  name: "برنامه های آینده",

  avatarUrl: princessImg,
  colors: {
    soft: colors.P50,
    hard: colors.N400A
  }
};

export const authors = [jake, BMO, finn, princess];

export const quotes = [
  {
    id: "1",
    content: "افزایش تولیدات شرکت به سه برابر",
    author: BMO
  },
  {
    id: "2",
    content:
      "افتتاح شعبه جدید در دبی",
    author: jake
  },
  {
    id: "3",
    content: "امضای قرارداد با شرکت های خاور میانه",
    author: jake
  },
  {
    id: "4",
    content: "انجام کارهای عقب افتاده",
    author: finn
  },
  {
    id: "5",
    content: "پیروزی در مناقصه رایان نور آبان ماه",
    author: finn
  },
  {
    id: "6",
    content: "ایجاد سیستم کنترل مرکزی شرکت های تابعه",
    author: princess
  },
  {
    id: "7",
    content: " کسب رتبه یک شورایعالی انفورماتیک ",
    author: princess
  },
  {
    id: "8",
    content:
      "People make mistakes. It's all a part of growing up and you never really stop growing",
    author: finn
  },

];


let idCount = quotes.length + 1;

export const getQuotes = (count) =>
  Array.from(quotes).map(() => {
    const random = quotes[Math.floor(Math.random() * quotes.length)];

    const custom = {
      ...random,
      id: `G${idCount++}`
    };

    return custom;
  });

export const getAuthors = (count) =>
  Array.from(authors).map(() => {
    const random = authors[Math.floor(Math.random() * authors.length)];

    const custom = {
      ...random,
      id: `author-${idCount++}`
    };

    return custom;
  });

const getByAuthor = (author, items) =>
  items.filter((quote) => quote.author === author);

export const authorQuoteMap: QuoteMap = authors.reduce(
  (previous, author) => ({
    ...previous,
    [author.name]: getByAuthor(author, quotes)
  }),
  {}
);

export const generateQuoteMap = (quoteCount) =>
  authors.reduce(
    (previous, author) => ({
      ...previous,
      [author.name]: getQuotes(quoteCount / authors.length)
    }),
    {}
  );
